"use strict";(self.webpackChunkchatgpt_shortcut=self.webpackChunkchatgpt_shortcut||[]).push([["8953"],{65400(n,t,e){e.r(t),e.d(t,{default:()=>i});var r=e(74848),o=e(47643),s=JSON.parse('{"id":187,"bn":{"title":"PPT \u09B0\u09C2\u09AA\u09B0\u09C7\u0996\u09BE \u09A4\u09C8\u09B0\u09BF \u0995\u09B0\u09C1\u09A8","prompt":"The entire conversation and instructions should be provided in Bengali. \u5E2E\u6211\u5236\u4F5C\u4E00\u7BC7\u5185\u5BB9\u4E3A\u300A\u4E3B\u9898\u300BPPT\uFF0C\u8981\u6C42\u5982\u4E0B\uFF1A\\n\u7B2C\u4E00\u3001\u4E00\u5B9A\u8981\u4F7F\u7528\u4E2D\u6587\u3002\\n\u7B2C\u4E8C\u3001\u9875\u9762\u5F62\u5F0F\u6709 3 \u79CD\uFF0C\u5C01\u9762\u3001\u76EE\u5F55\u3001\u5217\u8868\u3002\\n\u7B2C\u4E09\u3001\u76EE\u5F55\u9875\u8981\u5217\u51FA\u5185\u5BB9\u5927\u7EB2\u3002\\n\u7B2C\u56DB\u3001\u6839\u636E\u5185\u5BB9\u5927\u7EB2\uFF0C\u751F\u6210\u5BF9\u5E94\u7684 PPT \u5217\u8868\u9875\uFF0C\u6BCF\u4E00\u9875 PPT \u5217\u8868\u9875\u4F7F\u7528=====\u5217\u8868=====\u5F00\u5934\u3002\\n\u7B2C\u4E94\u3001\u5C01\u9762\u9875\u683C\u5F0F\u5982\u4E0B\uFF1A\\n=====\u5C01\u9762=====\\n# \u4E3B\u6807\u9898\\n## \u526F\u6807\u9898\\n\u6F14\u8BB2\u4EBA\uFF1A\u6211\u7684\u540D\u5B57\\n\u7B2C\u516D\u3001\u76EE\u5F55\u9875\u683C\u5F0F\u5982\u4E0B\uFF1A\\n=====\u76EE\u5F55=====\\n# \u76EE\u5F55\\n## CONTENT\\n1\u3001\u5185\u5BB9\\n2\u3001\u5185\u5BB9\\n\u7B2C\u4E03\u3001\u5217\u8868\u9875\u683C\u5F0F\u5982\u4E0B\uFF1A\\n=====\u5217\u8868=====\\n# \u9875\u9762\u4E3B\u6807\u9898\\n1\u3001\u8981\u70B9 1\\n\u8981\u70B9\u63CF\u8FF0\u5185\u5BB9\\n\u7B2C\u516B\u3001\u5217\u8868\u9875\u91CC\u7684\u8981\u70B9\u63CF\u8FF0\u5185\u5BB9\u662F\u5BF9\u8981\u70B9\u7684\u8BE6\u7EC6\u63CF\u8FF0\uFF0C10 \u4E2A\u5B57\u4EE5\u4E0A\uFF0C50 \u4E2A\u5B57\u4EE5\u5185\u3002\\n\\n\\n\u6700\u540E\uFF0C\u4E00\u5B9A\u8981\u4F7F\u7528\u4EE3\u7801\u5757\u56DE\u590D\u4F60\u751F\u6210\u7684\u5185\u5BB9\uFF0C\u5207\u8BB0\u5207\u8BB0\u3002.","description":"&quot;\u09A5\u09BF\u09AE&quot; \u09AC\u09BF\u09B7\u09AF\u09BC\u09AC\u09B8\u09CD\u09A4\u09C1 \u09B8\u09B9 \u098F\u0995\u099F\u09BF \u09AA\u09BF\u09AA\u09BF\u099F\u09BF \u09A4\u09C8\u09B0\u09BF \u0995\u09B0\u09A4\u09C7 \u0986\u09AE\u09BE\u0995\u09C7 \u09B8\u09BE\u09B9\u09BE\u09AF\u09CD\u09AF \u0995\u09B0\u09C1\u09A8, \u09AA\u09CD\u09B0\u09AF\u09BC\u09CB\u099C\u09A8\u09C0\u09AF\u09BC\u09A4\u09BE\u0997\u09C1\u09B2\u09BF \u09A8\u09BF\u09AE\u09CD\u09A8\u09B0\u09C2\u09AA: \u09AA\u09CD\u09B0\u09A5\u09AE\u09C7 \u0986\u09AA\u09A8\u09BE\u0995\u09C7 \u099A\u09BE\u0987\u09A8\u09BF\u099C \u09AC\u09CD\u09AF\u09AC\u09B9\u09BE\u09B0 \u0995\u09B0\u09A4\u09C7 \u09B9\u09AC\u09C7\u0964 \u09A6\u09CD\u09AC\u09BF\u09A4\u09C0\u09AF\u09BC\u09A4, \u09AA\u09C7\u099C, \u0995\u09AD\u09BE\u09B0, \u09AC\u09BF\u09B7\u09AF\u09BC\u09AC\u09B8\u09CD\u09A4\u09C1\u09B0 \u09B8\u09BE\u09B0\u09A3\u09C0 \u098F\u09AC\u0982 \u09A4\u09BE\u09B2\u09BF\u0995\u09BE 3 \u09AA\u09CD\u09B0\u0995\u09BE\u09B0\u0964 \u09A4\u09C3\u09A4\u09C0\u09AF\u09BC\u09A4, \u09AC\u09BF\u09B7\u09AF\u09BC\u09AC\u09B8\u09CD\u09A4\u09C1\u09B0 \u09AA\u09C3\u09B7\u09CD\u09A0\u09BE\u09AF\u09BC \u09AC\u09BF\u09B7\u09AF\u09BC\u09AC\u09B8\u09CD\u09A4\u09C1\u09B0 \u09B0\u09C2\u09AA\u09B0\u09C7\u0996\u09BE \u09A4\u09BE\u09B2\u09BF\u0995\u09BE\u09AD\u09C1\u0995\u09CD\u09A4 \u0995\u09B0\u09BE \u0989\u099A\u09BF\u09A4\u0964 \u099A\u09A4\u09C1\u09B0\u09CD\u09A5\u09A4, \u09AC\u09BF\u09B7\u09AF\u09BC\u09AC\u09B8\u09CD\u09A4\u09C1\u09B0 \u09B0\u09C2\u09AA\u09B0\u09C7\u0996\u09BE \u0985\u09A8\u09C1\u09AF\u09BE\u09AF\u09BC\u09C0, \u09B8\u0982\u09B6\u09CD\u09B2\u09BF\u09B7\u09CD\u099F PPT \u09A4\u09BE\u09B2\u09BF\u0995\u09BE \u09AA\u09C3\u09B7\u09CD\u09A0\u09BE \u09A4\u09C8\u09B0\u09BF \u0995\u09B0\u09C1\u09A8, PPT \u09A4\u09BE\u09B2\u09BF\u0995\u09BE \u09AA\u09C3\u09B7\u09CD\u09A0\u09BE\u09B0 \u09AA\u09CD\u09B0\u09A4\u09BF\u099F\u09BF \u09AA\u09C3\u09B7\u09CD\u09A0\u09BE ===== \u09A4\u09BE\u09B2\u09BF\u0995\u09BE ===== \u09A6\u09BF\u09AF\u09BC\u09C7 \u09B6\u09C1\u09B0\u09C1 \u09B9\u09AF\u09BC\u0964 \u09AA\u099E\u09CD\u099A\u09AE, \u0995\u09AD\u09BE\u09B0 \u09AA\u09C3\u09B7\u09CD\u09A0\u09BE\u09B0 \u09AC\u09BF\u09A8\u09CD\u09AF\u09BE\u09B8\u099F\u09BF \u09A8\u09BF\u09AE\u09CD\u09A8\u09B0\u09C2\u09AA: =====\u0995\u09AD\u09BE\u09B0===== # \u09AA\u09CD\u09B0\u09A7\u09BE\u09A8 \u09B6\u09BF\u09B0\u09CB\u09A8\u09BE\u09AE## \u09B8\u09BE\u09AC\u099F\u09BE\u0987\u099F\u09C7\u09B2 \u09B8\u09CD\u09AA\u09BF\u0995\u09BE\u09B0: \u0986\u09AE\u09BE\u09B0 \u09A8\u09BE\u09AE \u09B7\u09B7\u09CD\u09A0, \u09AC\u09BF\u09B7\u09AF\u09BC\u09AC\u09B8\u09CD\u09A4\u09C1\u09B0 \u09AA\u09C3\u09B7\u09CD\u09A0\u09BE\u09B0 \u09AC\u09BF\u09A8\u09CD\u09AF\u09BE\u09B8 \u09A8\u09BF\u09AE\u09CD\u09A8\u09B0\u09C2\u09AA: ===== \u09AC\u09BF\u09B7\u09AF\u09BC\u09AC\u09B8\u09CD\u09A4\u09C1===== #\u76EE\u5F55## \u09B8\u09BE\u09AE\u0997\u09CD\u09B0\u09C0 1. \u09AC\u09BF\u09B7\u09AF\u09BC\u09AC\u09B8\u09CD\u09A4\u09C1 2. \u09AC\u09BF\u09B7\u09AF\u09BC\u09AC\u09B8\u09CD\u09A4\u09C1 7. \u09A4\u09BE\u09B2\u09BF\u0995\u09BE \u09AA\u09C3\u09B7\u09CD\u09A0\u09BE\u09B0 \u09AC\u09BF\u09A8\u09CD\u09AF\u09BE\u09B8 \u09A8\u09BF\u09AE\u09CD\u09A8\u09B0\u09C2\u09AA: ===== \u09A4\u09BE\u09B2\u09BF\u0995\u09BE===== # \u09AA\u09C3\u09B7\u09CD\u09A0\u09BE\u09B0 \u09AA\u09CD\u09B0\u09A7\u09BE\u09A8 \u09B6\u09BF\u09B0\u09CB\u09A8\u09BE\u09AE 1, \u09AE\u09C2\u09B2 \u09AA\u09AF\u09BC\u09C7\u09A8\u09CD\u099F 1 \u0995\u09C0 \u09AA\u09AF\u09BC\u09C7\u09A8\u09CD\u099F \u09AC\u09B0\u09CD\u09A3\u09A8\u09BE \u09AC\u09BF\u09B7\u09AF\u09BC\u09AC\u09B8\u09CD\u09A4\u09C1 \u0985\u09B7\u09CD\u099F\u09AE, \u09A4\u09BE\u09B2\u09BF\u0995\u09BE\u09B0 \u09AA\u09C3\u09B7\u09CD\u09A0\u09BE\u09AF\u09BC \u09AE\u09C2\u09B2 \u09AA\u09AF\u09BC\u09C7\u09A8\u09CD\u099F\u09C7\u09B0 \u09AC\u09BF\u09AC\u09B0\u09A3 \u09B9\u09B2 \u09AC\u09BF\u09B7\u09AF\u09BC\u09AC\u09B8\u09CD\u09A4\u09C1 \u09B9\u09B2 \u09AE\u09C2\u09B2 \u09AA\u09AF\u09BC\u09C7\u09A8\u09CD\u099F\u09C7\u09B0 \u09AC\u09BF\u09B8\u09CD\u09A4\u09BE\u09B0\u09BF\u09A4 \u09AC\u09BF\u09AC\u09B0\u09A3, 10\u099F\u09BF\u09B0 \u09AC\u09C7\u09B6\u09BF \u0985\u0995\u09CD\u09B7\u09B0 \u098F\u09AC\u0982 50\u099F\u09BF\u09B0\u0993 \u0995\u09AE \u0985\u0995\u09CD\u09B7\u09B0\u0964 \u0985\u09AC\u09B6\u09C7\u09B7\u09C7, \u098F\u0995\u099F\u09BF \u0995\u09CB\u09A1 \u09AC\u09CD\u09B2\u0995\u09C7\u09B0 \u09B8\u09BE\u09A5\u09C7 \u0986\u09AA\u09A8\u09BE\u09B0 \u09A4\u09C8\u09B0\u09BF \u0995\u09B0\u09BE \u09B8\u09BE\u09AE\u0997\u09CD\u09B0\u09C0\u09B0 \u0989\u09A4\u09CD\u09A4\u09B0 \u09A6\u09BF\u09A4\u09C7 \u09AD\u09C1\u09B2\u09AC\u09C7\u09A8 \u09A8\u09BE, \u09AE\u09A8\u09C7 \u09B0\u09BE\u0996\u09AC\u09C7\u09A8\u0964","remark":"AI-\u0995\u09C7 \u098F\u0995\u099F\u09BF \u09AC\u09BF\u09B7\u09AF\u09BC\u09C7\u09B0 \u09B0\u09C2\u09AA\u09B0\u09C7\u0996\u09BE \u09A4\u09C8\u09B0\u09BF \u0995\u09B0\u09A4\u09C7 \u09A6\u09BF\u09A8, \u09A4\u09BE\u09B0\u09AA\u09B0 \u098F\u099F\u09BF\u0995\u09C7 \u09A8\u09BF\u09B0\u09CD\u09A6\u09BF\u09B7\u09CD\u099F \u09AE\u09BE\u09B0\u09CD\u0995\u09A1\u09BE\u0989\u09A8 \u09AC\u09BF\u09A8\u09CD\u09AF\u09BE\u09B8\u09C7 \u09B0\u09BE\u0996\u09C1\u09A8\u0964 PPT(\u09B8\u09CD\u09B2\u09BE\u0987\u09A1) \u0997\u09C1\u09A3\u09AE\u09BE\u09A8 \u09B6\u09C1\u09A7\u09C1\u09AE\u09BE\u09A4\u09CD\u09B0 \u09B0\u09C7\u09AB\u09BE\u09B0\u09C7\u09A8\u09CD\u09B8\u09C7\u09B0 \u099C\u09A8\u09CD\u09AF\u0964 @Asynchro-Epool \u09A5\u09C7\u0995\u09C7 \u0985\u09AC\u09A6\u09BE\u09A8\u0964"},"tags":["contribute"],"website":null,"count":4538,"metaDescription":"\u09A6\u09CD\u09B0\u09C1\u09A4 \u098F\u0995\u099F\u09BF \u0989\u09AA\u09B8\u09CD\u09A5\u09BE\u09AA\u09A8\u09BE \u09A4\u09C8\u09B0\u09BF \u0995\u09B0\u09A4\u09C7 \u09B9\u09AC\u09C7? \u098F\u0987 \u099A\u09C0\u09A8\u09BE \u09AD\u09BE\u09B7\u09BE\u09B0 \u09AA\u09CD\u09B0\u09AE\u09CD\u09AA\u099F\u099F\u09BF \u0986\u09AA\u09A8\u09BE\u0995\u09C7 \u09AE\u09BE\u09B0\u09CD\u0995\u09A1\u09BE\u0989\u09A8 \u09AB\u09B0\u09CD\u09AE\u09CD\u09AF\u09BE\u099F\u09C7 \u098F\u0995\u099F\u09BF \u09B8\u09AE\u09CD\u09AA\u09C2\u09B0\u09CD\u09A3 \u09AA\u09BF\u09AA\u09BF\u099F\u09BF \u09B0\u09C2\u09AA\u09B0\u09C7\u0996\u09BE \u09A4\u09C8\u09B0\u09BF \u0995\u09B0\u09A4\u09C7 \u09B8\u09BE\u09B9\u09BE\u09AF\u09CD\u09AF \u0995\u09B0\u09C7, \u09AF\u09BE\u09B0 \u09AE\u09A7\u09CD\u09AF\u09C7 \u0995\u09AD\u09BE\u09B0, \u09A1\u09BF\u09B0\u09C7\u0995\u09CD\u099F\u09B0\u09BF \u098F\u09AC\u0982 \u09AC\u09BF\u09B7\u09AF\u09BC\u09AC\u09B8\u09CD\u09A4\u09C1 \u09B8\u09CD\u09B2\u09BE\u0987\u09A1 \u09B0\u09AF\u09BC\u09C7\u099B\u09C7\u0964"}');function i(){return(0,r.jsx)(o.A,{prompt:s})}}}]);