"use strict";(self.webpackChunkchatgpt_shortcut=self.webpackChunkchatgpt_shortcut||[]).push([["9868"],{44133(e,t,o){o.r(t),o.d(t,{default:()=>i});var r=o(74848),n=o(47643),s=JSON.parse('{"id":248,"hi":{"title":"\u0906\u0908\u0908\u090F\u0932\u091F\u0940\u090F\u0938 \u0932\u0947\u0916\u0928\u2460","prompt":"Using the IELTS Writing Task scoring criteria, please respond to the following theme four times, each with a different level of proficiency: 9, 8, 7, and 6.\\n\\nThe theme is: \'Rich countries often give money to poorer countries, but it does not solve poverty. Therefore, developed countries should give other types of help to the poor countries rather than financial aid. To what extent do you agree or disagree? Your essay should be at least 250 words.\'\\n\\nFor each response, please begin with the title \'Response for Score X\', where X is the score level (9, 8, 7, or 6).\\n\\nAfter the four responses, explain why each response deserves its respective score, considering the following aspects: task achievement, coherence and cohesion, lexical resource, and grammatical range and accuracy. The entire conversation and instructions should be provided in Hindi. Reference specific sentences from each response to support your explanation.","description":"\u0906\u092A \u090F\u0915 \u092A\u0947\u0936\u0947\u0935\u0930 \u0905\u0902\u0917\u094D\u0930\u0947\u091C\u0940 \u092A\u094D\u0930\u094B\u092B\u0947\u0938\u0930 \u0939\u0948\u0902, \u0906\u0908\u0908\u090F\u0932\u091F\u0940\u090F\u0938 \u0932\u0947\u0916\u0928 \u0915\u093E\u0930\u094D\u092F \u0938\u094D\u0915\u094B\u0930\u093F\u0902\u0917 \u092E\u093E\u0928\u0926\u0902\u0921 \u0928\u093F\u092E\u094D\u0928\u0932\u093F\u0916\u093F\u0924 \u0939\u0948\u0902\u0964 \u092A\u0939\u0932\u093E \u0915\u0926\u092E \u0935\u093F\u0937\u092F \u0915\u094B &quot;\u0936\u0940\u0930\u094D\u0937\u0915&quot; \u0915\u0947 \u0930\u0942\u092A \u092E\u0947\u0902 \u0928\u093F\u0930\u094D\u0926\u093F\u0937\u094D\u091F \u0915\u0930\u0928\u093E \u0939\u0948 \u0914\u0930 9, 8, 7, \u0914\u0930 6 \u0915\u0947 \u0935\u093F\u092D\u093F\u0928\u094D\u0928 \u0905\u0902\u0915\u094B\u0902 \u0915\u0947 \u0905\u0928\u0941\u0938\u093E\u0930 \u091A\u093E\u0930 \u092C\u093E\u0930 \u092A\u094D\u0930\u0936\u094D\u0928\u094B\u0902 \u0915\u093E \u0909\u0924\u094D\u0924\u0930 \u0926\u0947\u0928\u093E \u0939\u0948\u0964 \u0909\u0924\u094D\u0924\u0930 \u0926\u0947\u0928\u0947 \u0938\u0947 \u092A\u0939\u0932\u0947 \u0906\u092A\u0915\u0947 \u0905\u0932\u0917-\u0905\u0932\u0917 \u0905\u0902\u0915\u094B\u0902 \u092E\u0947\u0902 \u090F\u0915 \u0936\u0940\u0930\u094D\u0937\u0915 \u0939\u094B\u0928\u093E \u091A\u093E\u0939\u093F\u090F [[\u0935\u093F\u092D\u093F\u0928\u094D\u0928 \u0905\u0902\u0915\u094B\u0902 \u0915\u0947 \u0909\u0924\u094D\u0924\u0930]] . \u092A\u0939\u0932\u0947 \u091A\u0930\u0923 \u0915\u0947 \u092C\u093E\u0926, \u092C\u093F\u0902\u0926\u0941 9, 8, 7, \u0914\u0930 6 \u092E\u0947\u0902, \u0906\u092A\u0915\u094B \u092F\u0939 \u092C\u0924\u093E\u0928\u093E \u0939\u094B\u0917\u093E \u0915\u093F \u0909\u0924\u094D\u0924\u0930 \u0915\u094B \u0905\u0927\u093F\u0915 \u092F\u093E \u0915\u092E \u0905\u0902\u0915 \u0915\u094D\u092F\u094B\u0902 \u0928\u0939\u0940\u0902 \u092E\u093F\u0932\u0947\u0902\u0917\u0947\u0964 \u0906\u092A\u0915\u094B &quot;\u0932\u0947\u0916\u0928 \u0915\u093E\u0930\u094D\u092F&quot; \u092E\u0947\u0902 \u0935\u093F\u0938\u094D\u0924\u093E\u0930 \u0938\u0947 \u0938\u092E\u091D\u093E\u0928\u0947 \u0915\u0947 \u0932\u093F\u090F \u0909\u0924\u094D\u0924\u0930 \u092E\u0947\u0902 \u0935\u093E\u0915\u094D\u092F \u0915\u094B \u0909\u0926\u094D\u0927\u0943\u0924 \u0915\u0930\u0928\u093E \u0939\u094B\u0917\u093E \u092A\u0942\u0930\u094D\u0923\u0924\u093E \u0938\u0941\u0938\u0902\u0917\u0924\u0924\u093E \u0914\u0930 \u0938\u093E\u092E\u0902\u091C\u0938\u094D\u092F \u0936\u092C\u094D\u0926\u093E\u0935\u0932\u0940 \u0907\u0928 \u092A\u094D\u0930\u0924\u093F\u0915\u094D\u0930\u093F\u092F\u093E\u0913\u0902 \u0915\u0947 \u092C\u0940\u091A \u0938\u092E\u0943\u0926\u094D\u0927\u093F, \u0935\u094D\u092F\u093E\u0915\u0930\u0923\u093F\u0915 \u0935\u093F\u0935\u093F\u0927\u0924\u093E \u0914\u0930 \u0938\u091F\u0940\u0915\u0924\u093E \u092E\u0947\u0902 \u0905\u0902\u0924\u0930&quot;\u0964 \u0926\u0942\u0938\u0930\u0947 \u0936\u092C\u094D\u0926\u094B\u0902 \u092E\u0947\u0902, \u0905\u092D\u0940 \u0905\u092A\u0928\u0947 9, 8, 7, \u0914\u0930 6 \u0909\u0924\u094D\u0924\u0930\u094B\u0902 \u0938\u0947 \u0935\u093E\u0915\u094D\u092F \u0909\u0926\u094D\u0927\u0943\u0924 \u0915\u0930\u0915\u0947 \u0938\u092E\u091D\u093E\u090F\u0902 \u0915\u093F \u0909\u0924\u094D\u0924\u0930 \u0915\u094B &quot;\u0932\u0947\u0916\u0928 \u0915\u093E\u0930\u094D\u092F \u092A\u0942\u0930\u094D\u0923\u0924\u093E \u0938\u0941\u0938\u0902\u0917\u0924\u0924\u093E \u0914\u0930 \u0938\u093E\u092E\u0902\u091C\u0938\u094D\u092F, \u0936\u093E\u092C\u094D\u0926\u093F\u0915 \u0938\u092E\u0943\u0926\u094D\u0927\u093F, \u0935\u094D\u092F\u093E\u0915\u0930\u0923\u093F\u0915 \u0935\u093F\u0935\u093F\u0927\u0924\u093E \u0914\u0930 \u0938\u091F\u0940\u0915\u0924\u093E&quot; \u0905\u0902\u0936 \u092E\u0947\u0902 \u090F\u0915 \u0928\u093F\u0936\u094D\u091A\u093F\u0924 \u0905\u0902\u0915 \u0915\u094D\u092F\u094B\u0902 \u092A\u094D\u0930\u093E\u092A\u094D\u0924 \u0939\u0941\u0906\u0964 \u0906\u0907\u090F \u091A\u0930\u0923 \u0926\u0930 \u091A\u0930\u0923 9 \u092C\u093F\u0902\u0926\u0941\u0913\u0902 \u092A\u0930 \u0935\u093F\u091A\u093E\u0930 \u0915\u0930\u0947\u0902: \u0932\u0947\u0916\u0928 \u0915\u093E\u0930\u094D\u092F\u094B\u0902 \u0915\u094B \u092A\u0942\u0930\u093E \u0915\u0930\u0928\u093E: \u0932\u0947\u0916\u0928 \u0915\u093E\u0930\u094D\u092F\u094B\u0902 \u0915\u0940 \u0938\u092D\u0940 \u0906\u0935\u0936\u094D\u092F\u0915\u0924\u093E\u0913\u0902 \u0915\u094B \u092A\u0942\u0930\u0940 \u0924\u0930\u0939 \u0938\u0947 \u092A\u0942\u0930\u093E \u0915\u0930\u0928\u093E, \u092A\u0942\u0930\u0940 \u0924\u0930\u0939 \u0938\u0947 \u0935\u093F\u0915\u0938\u093F\u0924 \u0932\u0947\u0916\u0928 \u0938\u093E\u092E\u0917\u094D\u0930\u0940 \u0915\u0940 \u0938\u0941\u0938\u0902\u0917\u0924\u0924\u093E \u0914\u0930 \u0938\u093E\u092E\u0902\u091C\u0938\u094D\u092F \u0915\u094B \u0938\u094D\u092A\u0937\u094D\u091F \u0930\u0942\u092A \u0938\u0947 \u092A\u094D\u0930\u0938\u094D\u0924\u0941\u0924 \u0915\u0930\u0928\u093E: \u0938\u093E\u092E\u0902\u091C\u0938\u094D\u092F\u092A\u0942\u0930\u094D\u0923 \u0938\u093E\u0927\u0928\u094B\u0902 \u0915\u093E \u0938\u094D\u0935\u0924\u0902\u0924\u094D\u0930 \u0930\u0942\u092A \u0938\u0947 \u0909\u092A\u092F\u094B\u0917 \u0915\u0930\u0928\u093E, \u0938\u0941\u0938\u0902\u0917\u0924 \u0930\u0942\u092A \u0938\u0947 \u0932\u093F\u0916\u0928\u093E, \u0915\u0941\u0936\u0932\u0924\u093E\u092A\u0942\u0930\u094D\u0935\u0915 \u0909\u092A\u0916\u0902\u0921\u094B\u0902 \u0915\u093E \u0909\u092A\u092F\u094B\u0917 \u0915\u0930\u0928\u093E \u0936\u092C\u094D\u0926\u093E\u0935\u0932\u0940 \u0915\u0940 \u0938\u092E\u0943\u0926\u094D\u0927\u093F: \u0935\u094D\u092F\u093E\u092A\u0915 \u0915\u093E \u0909\u092A\u092F\u094B\u0917 \u0915\u0930\u0928\u093E \u0936\u092C\u094D\u0926\u093E\u0935\u0932\u0940, \u0938\u094D\u0935\u093E\u092D\u093E\u0935\u093F\u0915 \u0930\u0942\u092A \u0938\u0947 \u091C\u091F\u093F\u0932 \u0936\u093E\u092C\u094D\u0926\u093F\u0915 \u0935\u093F\u0936\u0947\u0937\u0924\u093E\u0913\u0902 \u0915\u093E \u0909\u092A\u092F\u094B\u0917 \u0915\u0930\u0928\u0947 \u0914\u0930 \u0909\u0928\u092E\u0947\u0902 \u092E\u0939\u093E\u0930\u0924 \u0939\u093E\u0938\u093F\u0932 \u0915\u0930\u0928\u0947 \u092E\u0947\u0902 \u0938\u0915\u094D\u0937\u092E; \u091B\u094B\u091F\u0940 \u0924\u094D\u0930\u0941\u091F\u093F\u092F\u093E\u0902 \u0936\u093E\u092F\u0926 \u0939\u0940 \u0915\u092D\u0940 \u0939\u094B\u0924\u0940 \u0939\u0948\u0902, \u0914\u0930 \u0915\u0947\u0935\u0932 \u091F\u093E\u0907\u092A\u094B \u0924\u094D\u0930\u0941\u091F\u093F\u092F\u093E\u0902 \u0939\u094B\u0924\u0940 \u0939\u0948\u0902 \u0914\u0930 \u092F\u0939 \u0915\u0947\u0935\u0932 \u090F\u0915 \u0932\u093F\u092A\u093F\u0915\u0940\u092F \u0924\u094D\u0930\u0941\u091F\u093F \u0939\u0948 8 \u0905\u0902\u0915: \u0932\u0947\u0916\u0928 \u0915\u093E\u0930\u094D\u092F\u094B\u0902 \u0915\u094B \u092A\u0942\u0930\u093E \u0915\u0930\u0928\u093E: \u0932\u0947\u0916\u0928 \u0938\u093E\u092E\u0917\u094D\u0930\u0940 \u092A\u0942\u0930\u0940 \u0924\u0930\u0939 \u0938\u0947 \u0932\u0947\u0916\u0928 \u0915\u093E\u0930\u094D\u092F \u0915\u0940 \u0938\u092D\u0940 \u0906\u0935\u0936\u094D\u092F\u0915\u0924\u093E\u0913\u0902 \u0914\u0930 \u092E\u0941\u0916\u094D\u092F \u0938\u093E\u092E\u0917\u094D\u0930\u0940 \u0915\u094B \u0936\u093E\u092E\u093F\u0932 \u0915\u0930\u0924\u0940 \u0939\u0948 /\u092C\u093F\u0902\u0926\u0941\u0913\u0902 \u0915\u094B \u0938\u094D\u092A\u0937\u094D\u091F \u0930\u0942\u092A \u0938\u0947 \u0914\u0930 \u0909\u091A\u093F\u0924 \u0930\u0942\u092A \u0938\u0947 \u092A\u094D\u0930\u0938\u094D\u0924\u0941\u0924 \u0915\u093F\u092F\u093E \u0917\u092F\u093E \u0939\u0948, \u091C\u094B\u0930 \u0926\u093F\u092F\u093E \u0917\u092F\u093E \u0939\u0948 \u0914\u0930 \u0938\u092E\u091D\u093E\u092F\u093E \u0917\u092F\u093E \u0939\u0948\u0964 \u0938\u0941\u0938\u0902\u0917\u0924\u0924\u093E \u0914\u0930 \u0938\u093E\u092E\u0902\u091C\u0938\u094D\u092F: \u0938\u0942\u091A\u0928\u093E \u0914\u0930 \u0935\u093F\u091A\u093E\u0930\u094B\u0902 \u0915\u094B \u0924\u093E\u0930\u094D\u0915\u093F\u0915 \u0930\u0942\u092A \u0938\u0947 \u0935\u094D\u092F\u0935\u0938\u094D\u0925\u093F\u0924 \u0915\u093F\u092F\u093E \u0917\u092F\u093E \u0939\u0948, \u0935\u093F\u092D\u093F\u0928\u094D\u0928 \u0938\u093E\u092E\u0902\u091C\u0938\u094D\u092F \u0938\u093E\u0927\u0928\u094B\u0902 \u0915\u093E \u0909\u091A\u093F\u0924 \u0909\u092A\u092F\u094B\u0917, \u0916\u0902\u0921\u093F\u0924 \u0936\u092C\u094D\u0926\u093E\u0935\u0932\u0940 \u0938\u092E\u0943\u0926\u094D\u0927\u093F \u0915\u093E \u092A\u0942\u0930\u094D\u0923 \u0914\u0930 \u0909\u091A\u093F\u0924 \u0909\u092A\u092F\u094B\u0917: \u0938\u092E\u0943\u0926\u094D\u0927 \u0936\u092C\u094D\u0926\u093E\u0935\u0932\u0940 \u0915\u093E \u0927\u093E\u0930\u093E\u092A\u094D\u0930\u0935\u093E\u0939 \u0914\u0930 \u0932\u091A\u0940\u0932\u093E \u0909\u092A\u092F\u094B\u0917, \u0938\u091F\u0940\u0915 \u0905\u092D\u093F\u0935\u094D\u092F\u0915\u094D\u0924\u093F, \u0905\u0938\u093E\u092E\u093E\u0928\u094D\u092F \u0936\u092C\u094D\u0926\u093E\u0935\u0932\u0940 \u0915\u093E \u0915\u0941\u0936\u0932 \u0909\u092A\u092F\u094B\u0917, \u0932\u0947\u0915\u093F\u0928 \u0915\u092D\u0940-\u0915\u092D\u0940 \u0936\u092C\u094D\u0926 \u091A\u092F\u0928 \u0914\u0930 \u0938\u0902\u092F\u094B\u091C\u0928 \u0915\u0947 \u0938\u0902\u0926\u0930\u094D\u092D \u092E\u0947\u0902 \u0926\u093F\u0916\u093E\u0908 \u0926\u0947\u0924\u0947 \u0939\u0948\u0902 \u0915\u0941\u091B \u0917\u0932\u0924\u093F\u092F\u093E\u0901, \u0935\u0930\u094D\u0924\u0928\u0940 \u0914\u0930/\u092F\u093E \u0936\u092C\u094D\u0926 \u0928\u093F\u0930\u094D\u092E\u093E\u0923 \u092E\u0947\u0902 \u0924\u094D\u0930\u0941\u091F\u093F\u092F\u093E\u0901 \u0935\u094D\u092F\u093E\u0915\u0930\u0923\u093F\u0915 \u0935\u093F\u0935\u093F\u0927\u0924\u093E \u0914\u0930 \u0938\u091F\u0940\u0915\u0924\u093E: \u0935\u094D\u092F\u093E\u0915\u0930\u0923\u093F\u0915 \u0938\u0902\u0930\u091A\u0928\u093E\u0913\u0902 \u0915\u0940 \u090F\u0915 \u0935\u093F\u0938\u094D\u0924\u0943\u0924 \u0935\u093F\u0935\u093F\u0927\u0924\u093E \u0915\u093E \u0909\u092A\u092F\u094B\u0917 \u0915\u0930\u0924\u093E \u0939\u0948, \u0905\u0927\u093F\u0915\u093E\u0902\u0936 \u0935\u093E\u0915\u094D\u092F \u0938\u091F\u0940\u0915 \u0939\u094B\u0924\u0947 \u0939\u0948\u0902, \u0915\u0947\u0935\u0932 \u0915\u092D\u0940-\u0915\u092D\u093E\u0930 \u0924\u094D\u0930\u0941\u091F\u093F\u092F\u093E\u0901 \u092F\u093E \u0905\u092A\u0930\u094D\u092F\u093E\u092A\u094D\u0924\u0924\u093E\u090F\u0901 7 \u0905\u0902\u0915: \u0932\u0947\u0916\u0928 \u0915\u093E\u0930\u094D\u092F \u0938\u092E\u093E\u092A\u0928: \u0932\u0947\u0916\u0928 \u0915\u0940 \u0938\u093E\u092E\u0917\u094D\u0930\u0940 \u0932\u0947\u0916\u0928 \u0915\u093E\u0930\u094D\u092F \u0915\u0940 \u0906\u0935\u0936\u094D\u092F\u0915\u0924\u093E\u0913\u0902 \u0915\u094B \u0936\u093E\u092E\u093F\u0932 \u0915\u0930\u0924\u0940 \u0939\u0948, (\u0905\u0915\u093E\u0926\u092E\u093F\u0915) \u0938\u094D\u092A\u0937\u094D\u091F \u0930\u0942\u092A \u0938\u0947 \u092A\u094D\u0930\u092E\u0941\u0916 \u0930\u0941\u091D\u093E\u0928\u094B\u0902, \u092E\u0924\u092D\u0947\u0926\u094B\u0902 \u092F\u093E \u0935\u093F\u092D\u093F\u0928\u094D\u0928 \u091A\u0930\u0923\u094B\u0902 \u0915\u093E \u0905\u0935\u0932\u094B\u0915\u0928 \u092A\u094D\u0930\u0938\u094D\u0924\u0941\u0924 \u0915\u0930\u0924\u0940 \u0939\u0948, (\u092A\u094D\u0930\u0936\u093F\u0915\u094D\u0937\u0923) \u0938\u094D\u092A\u0937\u094D\u091F \u0930\u0942\u092A \u0938\u0947 \u0909\u0926\u094D\u0926\u0947\u0936\u094D\u092F \u092A\u094D\u0930\u0938\u094D\u0924\u0941\u0924 \u0915\u0930\u0924\u0940 \u0939\u0948 \u0932\u0947\u0916\u0928, \u0932\u0947\u0916\u0928 \u0915\u093E \u0938\u094D\u0935\u0930 \u0938\u0941\u0938\u0902\u0917\u0924 \u0914\u0930 \u0909\u092A\u092F\u0941\u0915\u094D\u0924 \u0939\u0948, \u092E\u0941\u0916\u094D\u092F \u0938\u093E\u092E\u0917\u094D\u0930\u0940 \u092A\u0930 \u091A\u0930\u094D\u091A\u093E \u0915\u0930\u0928\u0947 \u092E\u0947\u0902 \u0938\u0915\u094D\u0937\u092E \u0939\u0948 / \u092C\u093F\u0902\u0926\u0941\u0913\u0902 \u0915\u094B \u0938\u094D\u092A\u0937\u094D\u091F \u0930\u0942\u092A \u0938\u0947 \u092A\u094D\u0930\u0938\u094D\u0924\u0941\u0924 \u0915\u093F\u092F\u093E \u0917\u092F\u093E \u0939\u0948 \u0914\u0930 \u091C\u094B\u0930 \u0926\u093F\u092F\u093E \u0917\u092F\u093E \u0939\u0948, \u0932\u0947\u0915\u093F\u0928 \u0938\u0941\u0938\u0902\u0917\u0924\u0924\u093E \u0914\u0930 \u0938\u093E\u092E\u0902\u091C\u0938\u094D\u092F \u0905\u0927\u093F\u0915 \u092A\u0942\u0930\u094D\u0923 \u0930\u0942\u092A \u0938\u0947 \u0935\u093F\u0915\u0938\u093F\u0924 \u0928\u0939\u0940\u0902 \u0939\u0941\u0906 \u0939\u0948: \u091C\u093E\u0928\u0915\u093E\u0930\u0940 \u0914\u0930 \u0935\u093F\u091A\u093E\u0930\u094B\u0902 \u0915\u093E \u0924\u093E\u0930\u094D\u0915\u093F\u0915 \u0938\u0902\u0917\u0920\u0928, \u092A\u0942\u0930\u0947 \u092A\u093E\u0920 \u092E\u0947\u0902 \u0938\u094D\u092A\u0937\u094D\u091F \u0917\u0926\u094D\u092F \u092A\u094D\u0930\u0917\u0924\u093F, \u0938\u093E\u092E\u0902\u091C\u0938\u094D\u092F\u092A\u0942\u0930\u094D\u0923 \u0909\u092A\u0915\u0930\u0923\u094B\u0902 \u0915\u0940 \u090F\u0915 \u0936\u094D\u0930\u0943\u0902\u0916\u0932\u093E \u0915\u093E \u0909\u091A\u093F\u0924 \u0909\u092A\u092F\u094B\u0917, \u0939\u093E\u0932\u093E\u0902\u0915\u093F \u0915\u092D\u0940-\u0915\u092D\u0940 \u0915\u092E \u092F\u093E \u0905\u0924\u094D\u092F\u0927\u093F\u0915 \u0909\u092A\u092F\u094B\u0917, \u0915\u092D\u0940-\u0915\u092D\u0940 \u0932\u0917\u093E\u0924\u093E\u0930 \u0938\u094D\u092A\u0937\u094D\u091F\u0924\u093E \u092C\u0928\u093E\u090F \u0930\u0916\u0928\u0947 \u092E\u0947\u0902 \u0905\u0938\u092E\u0930\u094D\u0925 \u092F\u093E \u0938\u0902\u0926\u0930\u094D\u092D\u093E\u0924\u094D\u092E\u0915 \u0936\u092C\u094D\u0926\u093E\u0935\u0932\u0940 \u0938\u092E\u0943\u0926\u094D\u0927\u093F \u0915\u093E \u0909\u091A\u093F\u0924 \u0909\u092A\u092F\u094B\u0917: \u092A\u0930\u094D\u092F\u093E\u092A\u094D\u0924 \u0936\u092C\u094D\u0926\u093E\u0935\u0932\u0940 \u0915\u093E \u0909\u092A\u092F\u094B\u0917 \u0915\u0930\u0947\u0902, \u0915\u0941\u091B \u0932\u091A\u0940\u0932\u0947\u092A\u0928 \u0914\u0930 \u0938\u091F\u0940\u0915\u0924\u093E \u0915\u093E \u092A\u094D\u0930\u0926\u0930\u094D\u0936\u0928 \u0915\u0930\u0947\u0902, \u0905\u0938\u093E\u092E\u093E\u0928\u094D\u092F \u0936\u092C\u094D\u0926\u093E\u0935\u0932\u0940 \u0915\u093E \u0909\u092A\u092F\u094B\u0917 \u0915\u0930\u0947\u0902, \u0936\u0948\u0932\u0940 \u0914\u0930 \u0938\u0902\u092F\u094B\u091C\u0928 \u0915\u093E \u0915\u0941\u091B \u091C\u094D\u091E\u093E\u0928 \u0930\u0916\u0947\u0902 , \u0914\u0930 \u091A\u092F\u0928, \u0935\u0930\u094D\u0924\u0928\u0940 \u092E\u0947\u0902 \u0936\u092C\u094D\u0926\u094B\u0902 \u0915\u093E \u0909\u092A\u092F\u094B\u0917 \u0915\u0930\u0947\u0902 \u0914\u0930/\u092F\u093E \u0915\u092D\u0940-\u0915\u092D\u0940 \u0936\u092C\u094D\u0926 \u0928\u093F\u0930\u094D\u092E\u093E\u0923 \u092E\u0947\u0902 \u0917\u0932\u0924\u093F\u092F\u093E\u0901 \u0915\u0930 \u0938\u0915\u0924\u0947 \u0939\u0948\u0902 \u0935\u094D\u092F\u093E\u0915\u0930\u0923\u093F\u0915 \u0935\u093F\u0935\u093F\u0927\u0924\u093E \u0914\u0930 \u0938\u091F\u0940\u0915\u0924\u093E: \u0935\u093F\u092D\u093F\u0928\u094D\u0928 \u091C\u091F\u093F\u0932 \u0935\u094D\u092F\u093E\u0915\u0930\u0923\u093F\u0915 \u0938\u0902\u0930\u091A\u0928\u093E\u0913\u0902 \u0915\u093E \u0909\u092A\u092F\u094B\u0917 \u0915\u0930\u0947\u0902, \u0905\u0927\u093F\u0915\u093E\u0902\u0936 \u0935\u093E\u0915\u094D\u092F \u0938\u0939\u0940 \u0939\u0948\u0902, \u0935\u094D\u092F\u093E\u0915\u0930\u0923 \u0914\u0930 \u0935\u093F\u0930\u093E\u092E \u091A\u093F\u0939\u094D\u0928\u094B\u0902 \u092A\u0930 \u0905\u091A\u094D\u091B\u0940 \u092A\u0915\u0921\u093C \u0939\u0948, \u0932\u0947\u0915\u093F\u0928 \u0915\u092D\u0940-\u0915\u092D\u0940 \u0915\u0941\u091B \u0917\u0932\u0924\u093F\u092F\u093E\u0901 6 \u0905\u0902\u0915: \u0932\u0947\u0916\u0928 \u0915\u093E\u0930\u094D\u092F \u0915\u094B \u092A\u0942\u0930\u093E \u0915\u0930\u0928\u093E: \u0932\u0947\u0916\u0928 \u0915\u093E\u0930\u094D\u092F \u0915\u0947 \u0905\u0928\u0941\u0938\u093E\u0930 \u0930\u091A\u0928\u093E \u0915\u0930\u0928\u093E, (\u0905\u0915\u093E\u0926\u092E\u093F\u0915) \u0938\u093E\u0930\u093E\u0902\u0936 \u0915\u0947 \u0932\u093F\u090F \u0909\u092A\u092F\u0941\u0915\u094D\u0924 \u091C\u093E\u0928\u0915\u093E\u0930\u0940 \u0915\u093E \u091A\u092F\u0928 \u0915\u0930\u0928\u093E, (\u092A\u094D\u0930\u0936\u093F\u0915\u094D\u0937\u0923) \u0932\u0947\u0916\u0928 \u0915\u093E \u0909\u0926\u094D\u0926\u0947\u0936\u094D\u092F \u092E\u0942\u0932 \u0930\u0942\u092A \u0938\u0947 \u0938\u094D\u092A\u0937\u094D\u091F \u0939\u0948, \u0932\u0947\u0916\u0928 \u0915\u093E \u0932\u0939\u091C\u093E \u0915\u092D\u0940-\u0915\u092D\u0940 \u0938\u0941\u0938\u0902\u0917\u0924 \u0928\u0939\u0940\u0902 \u0939\u094B\u0924\u093E \u0939\u0948, \u092E\u0941\u0916\u094D\u092F \u0938\u093E\u092E\u0917\u094D\u0930\u0940 /\u092C\u093F\u0902\u0926\u0941 \u092A\u094D\u0930\u0938\u094D\u0924\u0941\u0924 \u0915\u093F\u092F\u093E \u0917\u092F\u093E \u0939\u0948 \u0914\u0930 \u092A\u0942\u0930\u0940 \u0924\u0930\u0939 \u0938\u0947 \u091C\u094B\u0930 \u0926\u093F\u092F\u093E \u0917\u092F\u093E \u0939\u0948, \u0932\u0947\u0915\u093F\u0928 \u0915\u092D\u0940-\u0915\u092D\u0940 \u0907\u0938\u092E\u0947\u0902 \u0905\u092A\u094D\u0930\u093E\u0938\u0902\u0917\u093F\u0915, \u0905\u0928\u0941\u091A\u093F\u0924, \u092F\u093E \u0917\u0932\u0924 \u0935\u093F\u0935\u0930\u0923 \u0936\u093E\u092E\u093F\u0932 \u0939\u094B\u0924\u0947 \u0939\u0948\u0902 \u0938\u0941\u0938\u0902\u0917\u0924\u0924\u093E \u0914\u0930 \u0938\u093E\u092E\u0902\u091C\u0938\u094D\u092F: \u091C\u093E\u0928\u0915\u093E\u0930\u0940 \u0914\u0930 \u0935\u093F\u091A\u093E\u0930\u094B\u0902 \u0915\u093E \u0938\u0941\u0938\u0902\u0917\u0924 \u0938\u0902\u0917\u0920\u0928, \u092E\u093E\u0930\u094D\u0917 \u0915\u0940 \u0938\u092E\u0917\u094D\u0930 \u0938\u094D\u092A\u0937\u094D\u091F\u0924\u093E, \u0938\u093E\u092E\u0902\u091C\u0938\u094D\u092F \u0915\u093E \u092A\u094D\u0930\u092D\u093E\u0935\u0940 \u0909\u092A\u092F\u094B\u0917, \u0932\u0947\u0915\u093F\u0928 \u0905\u0902\u0924\u0930-\u0935\u093E\u0915\u094D\u092F \u0914\u0930/\u092F\u093E \u0905\u0902\u0924\u0930-\u0935\u093E\u0915\u094D\u092F \u0938\u093E\u092E\u0902\u091C\u0938\u094D\u092F \u0915\u092D\u0940-\u0915\u092D\u0940 \u0917\u0932\u0924 \u092F\u093E \u092F\u093E\u0902\u0924\u094D\u0930\u093F\u0915, \u0915\u092D\u0940-\u0915\u092D\u0940 \u0932\u0917\u093E\u0924\u093E\u0930 \u0938\u094D\u092A\u0937\u094D\u091F\u0924\u093E \u092C\u0928\u093E\u090F \u0930\u0916\u0928\u0947 \u092E\u0947\u0902 \u0905\u0938\u092E\u0930\u094D\u0925 \u092F\u093E \u0938\u0902\u0926\u0930\u094D\u092D\u093E\u0924\u094D\u092E\u0915 \u0936\u092C\u094D\u0926\u093E\u0935\u0932\u0940 \u0915\u093E \u0909\u091A\u093F\u0924 \u0909\u092A\u092F\u094B\u0917 \u0938\u092E\u0943\u0926\u094D\u0927\u093F: \u0932\u0947\u0916\u0928 \u0915\u093E\u0930\u094D\u092F\u094B\u0902 \u0915\u0947 \u0932\u093F\u090F \u092A\u0930\u094D\u092F\u093E\u092A\u094D\u0924 \u0936\u092C\u094D\u0926\u093E\u0935\u0932\u0940 \u0915\u093E \u0909\u092A\u092F\u094B\u0917 \u0915\u0930\u0924\u093E \u0939\u0948, \u0915\u092E \u0938\u093E\u092E\u093E\u0928\u094D\u092F \u0936\u092C\u094D\u0926\u094B\u0902 \u0915\u093E \u0909\u092A\u092F\u094B\u0917 \u0915\u0930\u0928\u0947 \u0915\u093E \u092A\u094D\u0930\u092F\u093E\u0938 \u0915\u0930\u0924\u093E \u0939\u0948 \u0932\u0947\u0915\u093F\u0928 \u0915\u092D\u0940-\u0915\u092D\u0940 \u0917\u0932\u0924 \u0939\u094B\u0924\u093E \u0939\u0948, \u0935\u0930\u094D\u0924\u0928\u0940 \u0914\u0930/\u092F\u093E \u0936\u092C\u094D\u0926 \u0928\u093F\u0930\u094D\u092E\u093E\u0923 \u092E\u0947\u0902 \u0938\u092E\u0938\u094D\u092F\u093E\u090F\u0902 \u0939\u094B\u0924\u0940 \u0939\u0948\u0902 \u0924\u094D\u0930\u0941\u091F\u093F\u092F\u093E\u0902, \u0932\u0947\u0915\u093F\u0928 \u0928\u0939\u0940\u0902 \u0938\u0902\u091A\u093E\u0930 \u0915\u0940 \u0935\u093F\u0935\u093F\u0927\u0924\u093E \u0914\u0930 \u0938\u091F\u0940\u0915\u0924\u093E \u0915\u094B \u092A\u094D\u0930\u092D\u093E\u0935\u093F\u0924 \u0915\u0930\u0924\u0947 \u0939\u0948\u0902 \u0935\u094D\u092F\u093E\u0915\u0930\u0923: \u0938\u0930\u0932 \u0914\u0930 \u091C\u091F\u093F\u0932 \u0935\u093E\u0915\u094D\u092F \u092A\u0948\u091F\u0930\u094D\u0928 \u0915\u093E \u0938\u0902\u092F\u094B\u091C\u0928, \u0935\u094D\u092F\u093E\u0915\u0930\u0923 \u0914\u0930 \u0935\u093F\u0930\u093E\u092E \u091A\u093F\u0939\u094D\u0928 \u092E\u0947\u0902 \u0915\u0941\u091B \u0924\u094D\u0930\u0941\u091F\u093F\u092F\u093E\u0902, \u0932\u0947\u0915\u093F\u0928 \u092F\u0947 \u0924\u094D\u0930\u0941\u091F\u093F\u092F\u093E\u0902 \u0936\u093E\u092F\u0926 \u0939\u0940 \u0915\u092D\u0940 \u0938\u0902\u091A\u093E\u0930 \u0915\u094B \u092A\u094D\u0930\u092D\u093E\u0935\u093F\u0924 \u0915\u0930\u0924\u0940 \u0939\u0948\u0902","remark":"\u0938\u094D\u0915\u094B\u0930\u093F\u0902\u0917 \u0915\u0947 \u0915\u093E\u0930\u0923\u094B\u0902 \u0915\u0947 \u0938\u093E\u0925 \u090F\u0915 \u0939\u0940 \u0935\u093F\u0937\u092F \u092A\u0930 \u0905\u0932\u0917-\u0905\u0932\u0917 \u0938\u094D\u0915\u094B\u0930 \u0915\u0947 \u0938\u093E\u0925 \u0906\u0908\u0908\u090F\u0932\u091F\u0940\u090F\u0938 \u0928\u093F\u092C\u0902\u0927 \u0932\u093F\u0916\u0947\u0902\u0964 @fansi2020 \u0938\u0947 \u092F\u094B\u0917\u0926\u093E\u0928\u0964"},"tags":["contribute","pedagogy"],"website":null,"count":675,"metaTitle":"IELTS \u0928\u093F\u092C\u0902\u0927 \u0938\u094D\u0915\u094B\u0930\u0930: \u091F\u093E\u0938\u094D\u0915 2","metaDescription":"\u0906\u0908\u0908\u090F\u0932\u091F\u0940\u090F\u0938 \u0915\u0940 \u0924\u0948\u092F\u093E\u0930\u0940? \u0935\u093F\u0936\u093F\u0937\u094D\u091F \u0935\u093F\u0937\u092F\u094B\u0902 \u092A\u0930 6-9 \u0938\u094D\u0915\u094B\u0930 \u0915\u0930\u0928\u0947 \u0935\u093E\u0932\u0947 \u0928\u093F\u092C\u0902\u0927 \u0924\u0948\u092F\u093E\u0930 \u0915\u0930\u0947\u0902\u0964 \u0935\u093F\u0936\u094D\u0932\u0947\u0937\u0923 \u0915\u0930\u0947\u0902 \u0915\u093F..."}');function i(){return(0,r.jsx)(n.A,{prompt:s,currentLanguage:"hi"})}}}]);